import { createUseStyles } from 'react-jss';
var useSpacingStyles = createUseStyles({
    xxsmall: {
        minWidth: 2.5,
    },
    xsmall: {
        minWidth: 5,
    },
    small: {
        minWidth: 10,
    },
    'medium': {
        minWidth: 15,
    },
    '@global .vertical medium': {
        minWidth: 15,
        background: 'red'
    },
    large: {
        minWidth: 20,
    },
    xlarge: {
        minWidth: 25,
    },
    xxlarge: {
        minWidth: 30,
    }
});
export { useSpacingStyles, };
