import { createUseStyles } from 'react-jss';
var useFontSizeStyles = createUseStyles({
    'xsmall': {
        fontSize: 11
    },
    'small': {
        fontSize: 12
    },
    'default': {
        fontSize: 14,
        lineHeight: '20px',
        margin: '-6px 0 -4px 0',
    },
    'medium': {
        fontSize: 18,
        lineHeight: '25px',
        margin: '-7px 0 -5px 0',
    },
    'large': {
        fontSize: 24,
        lineHeight: '30px',
    },
    'xlarge': {
        fontSize: 32,
        lineHeight: '40px',
    },
});
export { useFontSizeStyles };
