import { createUseStyles } from 'react-jss';
var useFontSizeStyles = createUseStyles({
    'xsmall': { fontSize: 11 },
    'small': { fontSize: 12 },
    'default': { fontSize: 14 },
    'medium': { fontSize: 18 },
    'large': { fontSize: 24 },
    'xlarge': { fontSize: 32 },
});
export { useFontSizeStyles };
